mapper.select:
select concat(year(orderdate), '-', month(orderdate)) as 'yearlyTotal', sum(orderquantity * orderprice) as "totalPurchase"
from ingredientorder
where year(orderdate) = #{targetYear}
group by yearlyTotal;

select concat(year(orderdate), '-', month(orderdate)) as 'yearlyTotal', sum(quantity * price) as 'totalSale'
from ticketorder
inner join tickettype
on ticketorder.tickettypeid = tickettype.tickettypeid
where year(orderdate) = #{targetYear}
group by yearlyTotal;
