mapper.insert:
insert into ingredientorder (ingredientid, orderdate, orderquantity, supplier, orderprice)
values (#{ingredientid}, #{orderdate}, #{orderquantity}, #{supplier}, #{orderprice}) on duplicate key
update ingredientid = #{ingredientid}

mapper.update:
update ingredientorder (ingredientid, orderdate, orderquantity, supplier, orderprice)
values (#{ingredientid}, #{orderdate}, #{orderquantity}, #{supplier}, #{orderprice})
where orderid = #{orderid}
